mapper.select:
select n.id, title, author, e.name, e.code, ts
from note n left join emp e on e.id = n.author
order by ts desc, n.id desc limit #{skip}, #{count}

select n.id, title, body, author, e.name, ts
from note n left join emp e on e.id = n.author
where n.id = #{id}

mapper.update:
update Note
title=#{title}, body=#{body},
where id=#{id}
